import React, {useEffect, useReducer} from 'react';
import './../styles/hero.css';
import './../styles/reset.css';
import heroImage from './../img/asus-zenbook.png';
import axios from 'axios';

const initialState = {
  heroProduct: {},
  loading: true,
  error: ''
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'set-hero-product':
      return {
        loading: false,
        heroProduct: action.payload,
        error: ''
      }
    case "FETCH_ERROR":
      return {
        loading: false,
        error: 'Something went wrong!'
      }
    default:
      return state
  }
}

const Hero = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {heroProduct} = state;

  const renderHeroProduct = ({id, title, image, description, price}) => {
    if(heroProduct != null) {
      if(heroProduct.data) {
        return (
        <>
        <div className="info">
          <h3><span>Rp {price}</span></h3>
          <h1>{title}</h1>
          <div className="buttons">
             <button>Shop Now</button>
          </div>
        </div>
        <figure className="hero-image">
          <img src={getImage(image)} alt="Laptop"/>
        </figure>
        </>
        );
      }
    }
  }

  const getImage = image => {
    const source = require(`../img/${image}`);

    return source;
  }


  useEffect(() => {
    axios.get('/api/hero-product')
    .then(res => {
      dispatch({type: 'set-hero-product', payload: res.data});
    })
    .catch(error => {
      dispatch({type: 'FETCH_ERROR'});
    });
  }, []);

  return (
    <section className="hero-section" id="hero-section">
      <div className="hero-container">
        {
          heroProduct.data ? renderHeroProduct(heroProduct.data) : null
        }
      </div>
    </section>
  );
}

export default Hero;
