import React, {useEffect, useReducer} from 'react';
import './../styles/hero.css';
import './../styles/reset.css';
import heroImage from './../img/asus-zenbook.png';

const initialState = {
  heroProduct: {},
  loading: true,
  error: ''
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'set-hero-product':
      return {
        loading: false,
        heroProduct: action.payload,
        error: ''
      }
    case "FETCH_ERROR":
      return {
        loading: false,
        error: 'Something went wrong!'
      }
    default:
      return state
  }
}

const Hero = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const renderHeroProduct = ({id, title, image, description, price}) => {
    if(heroProduct != null) {
      if(heroProduct.data) {
        return (
          <div></div>
        );
      }
    }
  }

  useEffect(() => {
    axios.get('/api/hero-product')
    .then(res => {
      dispatch({type: 'set-hero-product', payload: res.data});
    })
    .catch(error => {
      dispatch({type: 'FETCH_ERROR'});
    });
  }, []);

  return (
    <section className="hero-section" id="hero-section">
      <div className="hero-container">
        <div className="info">
          <h1>Asus ZenBook</h1>
          <h3><span>Rp 4.500.000</span></h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Sunt, incidunt!</p>
          <div className="buttons">
             <button>Buy Now</button>
             <button className="detail-btn">Detail</button>
          </div>
        </div>
        <figure className="hero-image">
          <img src={heroImage} alt="Laptop"/>
        </figure>
      </div>
    </section>
  );
}

export default Hero;
