import React, {useReducer, useEffect, useContext} from 'react';
import image from '../img/asus-zenbook.png';
import axios from 'axios';

const initialState = {
  count: 0,
  products: [],
  error: '',
  loading: true,
  productInfo: [],
  error2: '',
  infoLoading: true
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'increment':
      return {
        ...state,
        count: state.count + 1
      }
    case 'decrement':
      return {
        ...state,
        count: (state.count - 1) < 0 ? 0 : state.count - 1
      }
    case 'set-products':
      return {
        ...state,
        products: action.payload,
        loading: false,
        error: '',
        infoLoading: false
      }
    case 'set-product-info': 
      return {
        ...state,
        productInfo: action.payload,
        loading: false,
        error: '',
        infoLoading: false
      }
    case 'set-loading':
      return {
        ...state,
        loading: action.loading
      }
    case 'set-info-loading':
      return {
        ...state,
        infoLoading: action.loading
      }
    case 'set-error2': 
      return {
         ...state,
         infoLoading: false,
         loading: false,
         error2: action.errorData
      }
    case "FETCH_ERROR":
      return {
        ...state,
        loading: false,
        error: action.errorData,
        infoLoading: false
      }
    default:
     return {
       state
     };
  }
}

const Product = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const { count, products = [], productInfo = [], loading, error, infoLoading, error2 } = state;
  const serverUrl = 'https://lovely-tan-dove.cyclic.app';

  const getImage = image => `/img/${image}`;

  const getDataFromServerAndSetDataToState = (url, action) => {
    axios.get(url)
    .then(res => {
      const data = res.data.productData;

      console.log(data)

      setDataToState(action, data, 'success');
    })
    .catch(err => {
      setDataToState('set-error', 'Something went wrong!', 'error');
      setDataToState('set-error2', 'Something went wrong!', 'error2');
      console.log(err);
    });

    return true;
  }

  const setDataToState = (action, data, condition) => {
    if(condition === 'success') {
      dispatch({type: action, payload: data});
    }

    if(condition === 'error') {
      dispatch({type: action, errorData: data});
    }

    if(condition === 'error2') {
      dispatch({type: action, errorData: data});
    }
  }

  const fetchData = () => {
    const res1 = getDataFromServerAndSetDataToState(`${serverUrl}/api/product-inf`, 'set-product-info');
    const res2 = getDataFromServerAndSetDataToState(`${serverUrl}/api/you-might-also-like-products`, 'set-products');

    if(!res1) {
      setDataToState('set-error', '', 'error');
    } else {
      setDataToState('set-error', 'Something went wrong!', 'error');
    }

    if(res2) {
      setDataToState('set-error2', '', 'error2');
    } else {
      setDataToState('set-error2', 'Something went wrong!', 'error2');
    }

    console.log('render');
  }

  useEffect(() => {
    fetchData();

    console.log('render useEffect')
    console.log(loading)
  }, []);   

  return (
  <>
    <section className="product">
    {
    error !== '' ? <h3 className="error-msg">{error}</h3> : (productInfo.length !== 0 && infoLoading === false) && productInfo.map(product => (
        <React.Fragment key={product.id}>
      <div className="product-images">
       <img src={getImage(product.image)}
            alt="laptop"
            className="main-image"/>
       <figure className="other-images">
         <img src={getImage(product.image)}  
              alt="laptop"/>
         <img src={getImage(product.image)}
              alt="laptop"/>
         <img src={getImage(product.image)}
              alt="laptop"/>
       </figure>
     </div>
     <div className="product-info">
       <h1>{product.title}</h1>
       <h3>Rp {product.price}</h3>
       <p>{product.description}</p>
       <div className="buttons">
         <div className="counter">
           <button className="decrement"
                   onClick={() => dispatch({type: 'decrement'})}>-</button>
           <p className="count">{count}</p>
           <button className="increment"
                   onClick={() => dispatch({type: 'increment'})}>+</button>
         </div>
         <button className="add-to-cart-btn">Add To Cart</button>
       </div>
     </div>
        </React.Fragment>
      )) 
    }
    </section>
    <section className="you-might-also-like">
      <h2>You Might Also Like</h2>
      <div className="card-list">
        {
          loading && (
           <h3 className="loading-product">Loading...</h3>
          )
        }
        {
          products.length !== 0 && products.map(({id, title, price, image}) => (
       <div className="card" key={id}>
        <figure className="card-image" style={{
          background: `url(${getImage(image)}) center / 70% no-repeat #eee`
        }}></figure>
        <div className="card-info">
          <h3 className="title">{title}</h3>
          <p className="price">Rp {price}</p>
        </div>
       </div>
       )) 
        }
      </div>
    </section>
  </>
  );
}

export default Product;
