import React, {useReducer, useEffect} from 'react';
import image from '../img/asus-zenbook.png';
import axios from 'axios';

const initialState = {
  count: 0,
  products: [],
  error: '',
  loading: true,
  productInfo: {}
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'increment':
      return {
        ...state,
        count: state.count + 1
      }
    case 'decrement':
      return {
        ...state,
        count: (state.count - 1) < 0 ? 0 : state.count - 1
      }
    case 'set-products':
      return {
        ...state,
        products: action.payload2,
        loading: false,
        error: ''
      }
    case 'set-product-info': 
      return {
        ...state,
        productInfo: action.payload1,
        loading: false,
        error: ''
      }
    case 'set-loading':
      return {
        ...state,
        loading: action.loading
      }
    case "FETCH_ERROR":
      return {
        ...state,
        loading: false,
        error: 'Something went wrong!'
      }
    default:
     return state;
  }
}

const Product = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {count, products, productInfo} = state;
  window.location.reload();

  const getDataFromServerAndSetState = (url, action, fieldName) => {
    axios.get(url)
    .then(res => {
      if(fieldName === 'product-info') {
        dispatch({type: action, payload1: res.data.productData});
      } else {
        dispatch({type: action, payload2: res.data.productData});
      }
    })
    .then(err => {
      dispatch({type: 'FETCH_ERROR'});
    });
  }

  const getImage = image => {
    console.log(image)
    const source = require(`./../img/${image}`);

    return source;
  }

  const renderProducts = ({id, title, price, image}) => {
      return (
       <div className="card" key={id}>
        <figure className="card-image" style={{
          background: `url(${getImage(image)}) center / 70% no-repeat #eee`
        }}></figure>
        <div className="card-info">
          <h3 className="title">{title}</h3>
          <p className="price">{price}</p>
        </div>
       </div>
      );
  }

  useEffect(() => {
    getDataFromServerAndSetState('/api/you-might-also-like-products', 'set-products', 'product');
    getDataFromServerAndSetState('/api/product-info', 'set-product-info', 'product-info');
  }, []);   

  return (
  <>
    <section className="product">
    {
      productInfo ? (
        <>
      <div className="product-images">
       <img src={getImage('asus-zenbook.png')}
            alt="laptop"
            className="main-image"/>
       <figure className="other-images">
         <img src={getImage('asus-zenbook.png')}
              alt="laptop"/>
         <img src={getImage('asus-zenbook.png')}
              alt="laptop"/>
         <img src={getImage('asus-zenbook.png')}
              alt="laptop"/>
       </figure>
     </div>
     <div className="product-info">
       <h1>{productInfo.title}</h1>
       <h3>{productInfo.price}</h3>
       <p>{productInfo.description}</p>
       <div className="buttons">
         <div className="counter">
           <button className="decrement"
                   onClick={() => dispatch({type: 'decrement'})}>-</button>
           <p className="count">{count}</p>
           <button className="increment"
                   onClick={() => dispatch({type: 'increment'})}>+</button>
         </div>
         <button className="add-to-cart-btn">Add To Cart</button>
       </div>
     </div>
        </>
      ) : null
    }
    </section>
    <section className="you-might-also-like">
      <h2>You Might Also Like</h2>
      <div className="card-list">
        {
          products ? products.map(product => {
            return renderProducts(product)
          }) : 'kosong'
        }
      </div>
    </section>
  </>
  );
}

export default Product;
