import React, {useReducer, useRef} from 'react';
import './../styles/reset.css';
import './../styles/our-product.css';

const initialState = {
  newProducts: [{}],
  onSale: [{}],
  featureProducts: [{}]
}

const reducer = (state, action) => {
  switch(action.type) {
    case "new-products":
      return {
        data: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "onsale":
      return {
        data: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "feature-products":
      return {
        data: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
  }
}

const OurProduct = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const cardListRef = useRef();

  const getDataByCategory = e => {
    const category = e.target.dataset.category;
    const data = dispatch({type: category})

