import React, {useReducer, useEffect} from 'react';
import image from '../img/asus-zenbook.png';
import axios from 'axios';

const initialState = {
  count: 0,
  products: [],
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'increment':
      return {
        ...state,
        count: state.count + 1
      }
    case 'decrement':
      return {
        ...state,
        count: (state.count - 1) < 0 ? 0 : state.count - 1
      }
    case 'set-products':
      return {
        ...state,
        products: action.payload,
        loading: false,
        error: ''
      }
    case 'set-loading':
      return {
        ...state,
        loading: action.loading
      }
    case "FETCH_ERROR":
      return {
        ...state,
        loading: false,
        error: 'Something went wrong!'
      }
    default:
     return state;
  }
}

const Product = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {count, products} = state;

  const getDataFromServer = url => {
    axios.get(url)
    .then(res => {
      return res.data.products;
    })
    .then(err => {
      return null;
    });
  }

  const renderProducts = products => {
    return (
       <div className="card">
        <figure className="card-image"></figure>
        <div className="card-info">
          <h3 className="title">HP Notebook</h3>
          <p className="price">Rp 5000000</p>
        </div>
       </div>
    );
  }

  useEffect(() => {
    const data = getDataFromServer('/api/you-might-also-like-products');

    if(data) return dispatch({type: 'set-products', payload: data});
    return dispatch({type: 'FETCH_ERROR'});
  }, []);   

  return (
  <>
    <section className="product">
     <div className="product-images">
       <img src={image}
            alt="laptop"
            className="main-image"/>
       <figure className="other-images">
         <img src={image}
              alt="laptop"/>
         <img src={image}
              alt="laptop"/>
         <img src={image}
              alt="laptop"/>
       </figure>
     </div>
     <div className="product-info">
       <h1>Asus ZenBook</h1>
       <h3>Rp 4500000</h3>
       <p>Lorem ipsum dolor sit amet, consectetur adipisicing elit. Pariatur, id.sit amet, consectetur aliquam mollitia ratione illum rem nesciunt molestias fuga laudantium eius.</p>
       <div className="buttons">
         <div className="counter">
           <button className="decrement"
                   onClick={() => dispatch({type: 'decrement'})}>-</button>
           <p className="count">{count}</p>
           <button className="increment"
                   onClick={() => dispatch({type: 'increment'})}>+</button>
         </div>
         <button className="add-to-cart-btn">Add To Cart</button>
       </div>
     </div>
    </section>
    <section className="you-might-also-like">
      <h2>You Might Also Like</h2>
      <div className="card-list">

      </div>
    </section>
  </>
  );
}

export default Product;
