import React, {useEffect, useState, createContext, useReducer} from 'react';
import {Routes, Route} from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import ProductInfo from './pages/ProductInfo';
import './styles/navbar.css';
import './styles/reset.css';
import './App.css';

export const RefreshComponentContext = createContext();
export const SetRefreshComponentContext = createContext();

const initialState = {
  refreshComponent: 0
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'increment-refresh-component': 
      return {
        refreshComponent: state.refreshComponent + 1
      }
    default:
      return state
  }
}

const App = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {refreshComponent} = state;

  useEffect(() => {
    console.log(refreshComponent)
    localStorage.setItem('refresh-component', refreshComponent);
  }, [refreshComponent]);

  return (
    <div className="App">
      <Navbar/>
      <RefreshComponentContext.Provider value={refreshComponent}>
      <SetRefreshComponentContext.Provider value={dispatch}>
      <Routes>
        <Route path="/"
               exact
               element={<Home/>}/>
        <Route path="/product-info"
               exact
               element={<ProductInfo/>}/>
      </Routes>
      </SetRefreshComponentContext.Provider>
      </RefreshComponentContext.Provider>
    </div>
  );
}

export default App;
