import React, {useReducer, useRef, useEffect} from 'react';
import './../styles/reset.css';
import './../styles/our-product.css';
import axios from 'axios';

const initialState = {
  newProducts: [],
  onsale: [],
  featureProducts: [],
  loading: true,
  error: '',
  selectedCategory: 'new-products'
}

const reducer = (state, action) => {
  switch(action.type) {
    case "set-new-products":
      return {
        newProducts: action.payloads,
        loading: false,
        error: ''
      }
    case "set-onsale":
      return {
        onsale: action.payloads,
        loading: false,
        error: ''
      }
    case "set-feature-products":
      return {
        featureProducts: action.payloads,
        loading: false,
        error: ''
      }
    case 'new-products':
      return {
        selectedCategory: 'new-products'
      }
    case 'onsale': 
      return {
        selectedCategory: 'onsale'
      }
    case 'feature-products':
      return {
        selectedCategory: 'feature-products'
      }
    case "FETCH_ERROR":
      return {
        loading: false,
        error: 'Something went wrong!'
      }
    default:
      return state
  }
}

const OurProduct = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {
    newProducts,
    onsale, 
    featureProducts,
    selectedCategory
  } = state;
  const cardListRef = useRef();
  let cards = '';

  const getDataByCategory = e => {
    const category = e.target.dataset.category;
    dispatch({type: category});

    if(selectedCategory === "new-products") {
      newProducts.map(product => {
        cardListRef.current.innerHTML = getCards(product);
      });
    } else {
      alert(newProducts)
    }

    if(selectedCategory === "onsale" && onsale.length !== 0) {
      onsale.map(product => {
        cardListRef.current.innerHTML = getCards(product);
      })
    }

    if(selectedCategory === "feature-products" && featureProducts.length !== 0) {
      alert(featureProducts)
      featureProducts.map(product => {
        cardListRef.current.innerHTML = getCards(product);
      })
    }

  }

  const getImage = image => {
    const source = require(`../img/${image}`);

    return source;
  }

  const getCards = ({image, title, price, id}) => {
    return cards += `
      <div className="card" key=${id}>
        <figure className="card-image" style={{
          background: 'url(${getImage(image)}) center / 70% no-repeat #eee'
        }}>
        </figure>
        <div className="card-info">
          <h3>${title}</h3>
          <p className="price">Rp ${price}</p>
        </div>
      </div>
    `;
  }

  useEffect(() => {
    axios.get('/api/new-products')
    .then(res => {
      dispatch({type: 'set-new-products', payloads: res.data})
    })
    .catch(error => {
      dispatch({type: 'FETCH_ERROR'})
    })

    axios.get('/api/onsale')
    .then(res => {
      dispatch({type: 'set-new-products', payloads: res.data})
    })
    .catch(error => {
      dispatch({type: 'FETCH_ERROR'})
    })

    axios.get('/api/feature-products')
    .then(res => {
      dispatch({type: 'set-new-products', payloads: res.data})
    })
    .catch(error => {
      dispatch({type: 'FETCH_ERROR'})
    })
  }, [newProducts, onsale, featureProducts, selectedCategory]);

  return (
    <section className="our-product">
      <div className="categories">
        <h4 className="selected"
          data-category="new-products" onClick={getDataByCategory}>New Products</h4>
        <h4 data-category="onsale" onClick={getDataByCategory}>Onsale</h4>
        <h4 data-category="feature-products" onClick={getDataByCategory}>Feature Products</h4>
      </div>
      <div className="card-list" ref={cardListRef}>

      </div>
    </section>
  );
}

export default OurProduct;
