import React, {useEffect, useState, createContext, useReducer} from 'react';
import axios from 'axios';
import {Route, Routes, useNavigate} from 'react-router-dom';

  const navigate = () => useNavigate();
  const serverUrl = () => 'http://localhost:5000';

  export const addProductsToState = async (endpoint, type, dispatch) => {
    const response = await getProducts(endpoint);

    if(response === 'FETCH_ERROR') {
      dispatch({type: 'FETCH_ERROR'});
    } 

    if(response.length !== 0) {
      dispatch({type: type, payloads: response})
    }

    console.log(response)
  }

  export const getProducts = async endpoint => {
    try {
      const response = await axios.get(`${serverUrl}${endpoint}`);

      return response.data.productData;
    } catch(err) {
      return 'FETCH_ERROR';
    }
  }

  export const sendDataToServerAndMovePage = (url, {id, title, image, description, price}) => {
    console.log('send data to server')
    const productInfoUrl = `${serverUrl}/api/product-info`;

    axios.post(productInfoUrl, {
      title,
      id,
      image,
      description,
      price
    })
    .then(res => {
      console.log(res.data.msg);

      if(url === '') return window.location.reload();
      return movePage(url);
    })
    .then(err => {
      console.log('Failed send data to server!');
    });

    console.log(title)
  }

  export const movePage = url => {
    navigate(url);
  }

  export const getImage = image => `/img/${image}`;
