import React, {useReducer, useRef} from 'react';
import './../styles/reset.css';
import './../styles/our-product.css';

const initialState = {
  newProducts: [],
  onSale: [],
  featureProducts: []
}

const reducer = (state, action) => {
  switch(action.type) {
    case "new-products":
      return {
        newProducts: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          },
          {
            id: 2,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "onsale":
      return {
        onsale: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "feature-products":
      return {
        featureProducts: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
  }
}

const OurProduct = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {newProducts, onsale, featureProducts} = state;
  const getDataByCategory = e => {
    const category = e.target.dataset.category;
    dispatch({type: category});
  }

  const getImage = image => {
    const source = require(`../img/${image}`);

    return source;
  }

  const showCards = (image, title, price) => {
    return (
      <div className="card" key={title}>
        <figure className="card-image" style={{
          background: `url(${getImage(image)}) center / 70% no-repeat #eee`
        }}>
        </figure>
        <div className="card-info">
          <h3>{title}</h3>
          <p className="price">{price}</p>
        </div>
      </div>
    );
  }

  return (
    <section className="our-product">
      <div className="categories">
        <h4 className="selected"
          data-category="new-products" onClick={getDataByCategory}>New Products</h4>
        <h4 data-category="onsale" onClick={getDataByCategory}>Onsale</h4>
        <h4 data-category="feature-products" onClick={getDataByCategory}>Feature Products</h4>
      </div>
      <div className="card-list">
        {newProducts.length !== 0 ? newProducts.map(({ title, image, price }) => (
           showCards(image, title, price)
        )) : null}
      </div>
    </section>
  );
}

export default OurProduct;
