import React, {useReducer} from 'react';
import './../styles/reset.css';
import './../styles/our-product.css';

const initialState = {
  newProducts: [{}],
  onSale: [{}],
  featureProducts: [{}]
}

const reducer = (state, action) => {
  switch(action.type) {
    case "new-products":
      return {
        data: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "onsale":
      return {
        data: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "feature-products":
      return {
        data: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
  }
}

const OurProduct = () => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <section className="our-product">
      <div class="categories">
        <h4 className="selected"
            data-category="new-products">New Products</h4>
        <h4 data-category="onsale">Onsale</h4>
        <h4 data-category="feature-products">Feature Products</h4>
      </div>
      <div className="card-list">
        <div className="card">
          <figure className="card-image"></figure>
          <div class="card-info">
            <h3>Asus Vivobook</h3>
            <p className="price">Rp 2.000.000</p>
          </div>
        </div>
      </div>
    </section>
  );
}

export default OurProduct;
