import React, {useEffect, useState, createContext} from 'react';
import {Routes, Route} from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import ProductInfo from './pages/ProductInfo';
import './styles/navbar.css';
import './styles/reset.css';
import './App.css';

export const RefreshComponentContext = createContext();
export const SetRefreshComponentContext = createContext();

const App = () => {
  const [refreshComponent, setRefreshComponent] = useState(0);  

  useEffect(() => {
    console.log(refreshComponent)
  }, []);

  return (
    <div className="App">
      <Navbar/>
      <RefreshComponentContext.Provider value={refreshComponent}>
      <SetRefreshComponentContext.Provider value={setRefreshComponent}>
      <Routes>
        <Route path="/"
               exact
               element={<Home/>}/>
        <Route path="/product-info"
               exact
               element={<ProductInfo/>}/>
      </Routes>
      </SetRefreshComponentContext.Provider>
      </RefreshComponentContext.Provider>
    </div>
  );
}

export default App;
