import React, {useReducer, useRef, useEffect} from 'react';
import './../styles/reset.css';
import './../styles/our-product.css';
import axios from 'axios';

const initialState = {
  newProducts: [],
  onsale: [],
  featureProducts: [],
  loading: true,
  error: '',
  selectedCategory: ''
}

const reducer = (state, action) => {
  switch(action.type) {
    case "set-new-products":
      return {
        newProducts: action.payloads
      }
    case "set-onsale":
      return {
        onsale: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "set-feature-products":
      return {
        featureProducts: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case 'new-products':
      return {
        selectedCategory: 'new-products'
      }
    case "FETCH_ERROR":
      return {
        loading: false,
        error: 'Something went wrong!'
      }
    default:
      return state
  }
}

const OurProduct = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {
    newProducts,
    onsale, 
    featureProducts,
    selectedCategory
  } = state;
  const getDataByCategory = e => {
    const category = e.target.dataset.category;
    dispatch({type: category});
  }

  const getImage = image => {
    const source = require(`../img/${image}`);

    return source;
  }

  const showCards = ({image, title, price, id}) => {
    return (
      <div className="card" key={id}>
        <figure className="card-image" style={{
          background: `url(${getImage(image)}) center / 70% no-repeat #eee`
        }}>
        </figure>
        <div className="card-info">
          <h3>{title}</h3>
          <p className="price">Rp {price}</p>
        </div>
      </div>
    );
  }

  useEffect(() => {
    axios.get('/api/new-products')
    .then(res => {
      dispatch({type: 'set-new-products', payload: res.data})
    })
    .catch(error => {
      dispatch({type: 'FETCH_ERROR'})
    })
  }, []);

  return (
    <section className="our-product">
      <div className="categories">
        <h4 className="selected"
          data-category="new-products" onClick={getDataByCategory}>New Products</h4>
        <h4 data-category="onsale" onClick={getDataByCategory}>Onsale</h4>
        <h4 data-category="feature-products" onClick={getDataByCategory}>Feature Products</h4>
      </div>
      <div className="card-list">
        {selectedCategory === "new-products" ? newProducts.map(product => (showCards(product))) : 'kosong'}
        {onsale ? onsale.map(product => (showCards(product))) : null}
        {featureProducts ? featureProducts.map(product => (showCards(product))) : null}
      </div>
    </section>
  );
}

export default OurProduct;
