import React, {useReducer, useRef} from 'react';
import './../styles/reset.css';
import './../styles/our-product.css';

const initialState = {
  newProducts: [{}],
  onSale: [{}],
  featureProducts: [{}]
}

const reducer = (state, action) => {
  switch(action.type) {
    case "new-products":
      return {
        newProducts: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "onsale":
      return {
        onsale: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
    case "feature-products":
      return {
        featureProducts: [
          {
            id: 1,
            title: 'Asus ZenBook',
            image: 'asus-zenbook.png',
            price: 4500000
          }
        ]
      }
  }
}

const OurProduct = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {newProducts, onsale, featureProducts} = state;
  const cardListRef = useRef();

  const getDataByCategory = e => {
    const category = e.target.dataset.category;
    dispatch({type: category});
    let cards = '';

    if(newProducts) {
      newProducts.forEach(({title, image, price}) => {
        return cards += `
        <div className="card">
          <figure className="card-image"></figure>
          <div class="card-info">
            <h3>${title}</h3>
            <p className="price">${price}</p>
          </div>
        </div>`;
      });
    }

    cardListRef.innerHTML = cards;
  }

  return (
    <section className="our-product">
      <div className="categories">
        <h4 className="selected"
            data-category="new-products" onClick={getDataByCategory}>New Products</h4>
        <h4 data-category="onsale">Onsale</h4>
        <h4 data-category="feature-products">Feature Products</h4>
      </div>
      <div className="card-list" ref={cardListRef}>

      </div>
    </section>
  );
}

export default OurProduct;
