import React, {useReducer, useEffect} from 'react';
import image from '../img/asus-zenbook.png';
import axios from 'axios';

const initialState = {
  count: 0,
  products: [],
  error: '',
  loading: true,
  productInfo: []
}

const reducer = (state, action) => {
  switch(action.type) {
    case 'increment':
      return {
        ...state,
        count: state.count + 1
      }
    case 'decrement':
      return {
        ...state,
        count: (state.count - 1) < 0 ? 0 : state.count - 1
      }
    case 'set-products':
      return {
        ...state,
        products: action.payload,
        loading: false,
        error: ''
      }
    case 'set-product-info': 
      return {
        ...state,
        productInfo: action.payload,
        loading: false,
        error: ''
      }
    case 'set-loading':
      return {
        ...state,
        loading: action.loading
      }
    case "FETCH_ERROR":
      return {
        ...state,
        loading: false,
        error: action.errorData
      }
    default:
     return {
       state
     };
  }
}

const Product = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const {count, products, productInfo, loading, error} = state;

  const getDataFromServerAndSetDataToState = (url, action) => {
    axios.get(url)
    .then(res => {
      const data = res.data.productData;
      setDataToState(action, data, 'success');
    })
    .then(err => {
      console.log(err)
      setDataToState('FETCH_ERROR', 'Something went wrong!', 'error');
    });
  }

  const setDataToState = (action, data, condition) => {
    if(condition === 'success') {
      dispatch({type: action, payload: data});
    }

    if(condition === 'error') {
      dispatch({type: action, errorData: data});
    }
  }

  const getImage = image => `/img/${image}`;

  const renderProducts = ({id, title, price, image}) => {
      return (
       <div className="card" key={id}>
        <figure className="card-image" style={{
          background: `url(${getImage(image)}) center / 70% no-repeat #eee`
        }}></figure>
        <div className="card-info">
          <h3 className="title">{title}</h3>
          <p className="price">{price}</p>
        </div>
       </div>
      );
  }

  const fetchData = async () => {
    getDataFromServerAndSetDataToState('/api/you-might-also-like-products', 'set-products');
    getDataFromServerAndSetDataToState('/api/product-info', 'set-product-info');

    console.log('render');
  }

  useEffect(() => {
    fetchData();
    return () => fetchData();
  }, []);   

  return (
  <>
    <section className="product">
    {
      productInfo.length !== 0 && productInfo.map(product => (
        <React.Fragment key={product.id}>
      <div className="product-images">
       <img src={getImage(product.image)}
            alt="laptop"
            className="main-image"/>
       <figure className="other-images">
         <img src={getImage(product.image)}  
              alt="laptop"/>
         <img src={getImage(product.image)}
              alt="laptop"/>
         <img src={getImage(product.image)}
              alt="laptop"/>
       </figure>
     </div>
     <div className="product-info">
       <h1>{product.title}</h1>
       <h3>{product.price}</h3>
       <p>{product.description}</p>
       <div className="buttons">
         <div className="counter">
           <button className="decrement"
                   onClick={() => dispatch({type: 'decrement'})}>-</button>
           <p className="count">{count}</p>
           <button className="increment"
                   onClick={() => dispatch({type: 'increment'})}>+</button>
         </div>
         <button className="add-to-cart-btn">Add To Cart</button>
       </div>
     </div>
        </React.Fragment>
      )) 
    }
    </section>
    <section>
    {
      loading && (
        <h3>Loading...</h3>
      )
    }
    </section>
    <section className="you-might-also-like">
      <h2>You Might Also Like</h2>
      <div className="card-list">
        {
          products.length !== 0 && products.map(product => {
            return renderProducts(product)
          })
        }
      </div>
    </section>
  </>
  );
}

export default Product;
